import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, } from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';







export default function Approveleave() {
    const [data, setData] = useState([]);
    const userdata = useLocation();
    const authtoken = userdata.state.authtoken;


    useEffect(() => {

        axios.get("/fetchallleaves",
            {

                headers: {
                    "AdminAuthToken": authtoken
                }
            })
            .then((res) => {
                console.log(res.data);
                setData(res.data);

            })
            .catch(err => console.error(err));
    }, [])

    const ApproveLeave = (id) => {

        axios.get("/approveleave/" + item._id,
            {

                headers: {
                    "AdminAuthToken": authtoken
                }
            })
            .then((res) => {
                console.log(res.data);


            })
            .catch(err => console.error(err));
    }


    return (
        <div className='viewatt-table-div'>

            <table className="table table-success table-striped ">

                <thead>
                    <tr>
                        <th scope="col">No</th>
                        <th scope='col'>User id</th>
                        <th scope='col'>Status</th>
                        <th scope='col'>Action</th>

                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((item, index) => {

                            return <tr key={item._id}>
                                <th scope="row">{index + 1}</th>
                                <td>{item.user}</td>
                                <td>{item.status}</td>

                                <td>
                                    <button className='btn btn-primary' onClick={ ApproveLeave()}>
                                        Approve Leave
                                    </button>

                                </td>
                            </tr>

                        })
                    }

                </tbody>
            </table>


        </div >
    )
}
