import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useEffect } from 'react';
import axios from 'axios';


export default function Mainpage() {
    const [name ,setName]=useState("");
    const [imagepath,setImagepath]=useState("logo.png");

    const data = useLocation();
    const authtoken = data.state.authtoken;

    useEffect(() => {
        axios
            .get("http://localhost:4000/getuser", {
                headers: {
                    "AuthToken":authtoken
                }
            })
            .then((res)=>{
                if (res.status==200 && res.data !="") {
                    setName(res.data.name);


                } else {

                }
            })
            .catch(err => console.error(err));
    }, []);


//Function to mark Attendence

const MarkAttendence=()=>{
   return axios .post("http://localhost:4000/markattendence",{
        status:"present",
      },
      {
        headers:{
            "AuthToken":authtoken
        }
      }).then((res)=>{
        console.log(res);
      })
      .catch(err => console.error(err));
}


    return (
        <>

            <div className='navbar'>
                <img height={"50px"} src={require("../images/"+imagepath)} />
                <div className='profile-div'>
                    <img className="rounded-circle" width={"50px"} height={"50px"} src={require("../images/image.webp")} />
                    <span >{name}</span>

                </div>

            </div>




            <div className='mainpage-div'>
                <button type="button" className="btn btn-primary ms-3" onClick={MarkAttendence}>Mark Attendence</button>
                <button type="button" className="btn btn-secondary ms-3">Mark Leave</button>
                <button type="button" className="btn btn-info ms-3" >View Attendence</button>
            </div>
        </>
    )
}
