import React, { useState ,useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';




export default function Updateattendance() {
    const [val, setVal] = useState("");
    const [datval, setDatval] = useState("2018-07-22");

    const data = useLocation();
    const authtoken = data.state.authtoken;
    const id = data.state.id;

    useEffect(() => {

        axios
            .get("/findoneattendance/" + id, {
                headers: {
                    "AdminAuthToken": authtoken
                }
            })
            .then((res) => {
                console.log(res);
                setVal(res.data.status);
                setDatval(res.data.markeddate)
            })
            .catch(err => console.error(err));

    }, [])
    return (
        <>

            <div>
                <input type="date" value={datval} onChange={(event) => {
                    setDatval(event.target.value);
                    console.log(event.target.value);
                }} />
                <label >Choose Status</label>

                <select value={val} onChange={(e) => {
                    console.log(e.target.value);
                    setVal(e.target.value)

                }}>
                    <option></option>
                    <option value="present">Present</option>
                    <option value="absent">Absent</option>
                    <option value="leave">On Leave</option>

                </select>
                <button className='btn btn-primary'>Update Attendence</button>
            </div>
        </>
    )
}
