import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, } from '@fortawesome/free-solid-svg-icons';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';



export default function Approveleave() {
    const [data, setData] = useState([]);
    const userdata = useLocation();
    const authtoken = userdata.state.authtoken;
    const [open, setOpen] = React.useState(false);
    const [id ,setId]=useState("");

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    useEffect(() => {

        axios.get("/fetchallleaves",
            {

                headers: {
                    "AdminAuthToken": authtoken
                }
            })
            .then((res) => {
                console.log(res.data);
                setData(res.data);

            })
            .catch(err => console.error(err));
    }, []);


    //Approve Leave function
    const ConfirmLeave = (id) => {
        axios.get("/approveleave/" + id,
            {

                headers: {
                    "AdminAuthToken": authtoken
                }
            })
            .then((res) => {
                console.log(res.data);


            })
            .catch(err => console.error(err));

    }
    return (
        <div className='viewatt-table-div'>

            <table className="table table-success table-striped ">

                <thead>
                    <tr>
                        <th scope="col">No</th>
                        <th scope='col'>User id</th>
                        <th scope='col'>Status</th>
                        <th scope='col'>Action</th>

                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((item, index) => {

                            return <tr key={item._id}>
                                <th scope="row">{index + 1}</th>
                                <td>{item.user}</td>
                                <td>{item.status}</td>

                                <td>
                                    <button className='btn btn-primary' onClick={() => {
                                        handleClickOpen();
                                        setId(item._id);

                                    }}>
                                        Approve Leave
                                    </button>

                                </td>
                            </tr>

                        })
                    }

                </tbody>
            </table>

            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    {"Are you sure you want to Approve this Leave ?"}
                </DialogTitle>
                {/* <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Let Google help apps determine location. This means sending anonymous
                        location data to Google, even when no apps are running.
                    </DialogContentText>
                </DialogContent> */}
                <DialogActions>
                    <Button onClick={handleClose}>Disagree</Button>
                    <Button onClick={ConfirmLeave(id)} autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>

        </div >
    )
}
