import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { useLocation } from 'react-router-dom';
export default function Singleuserreport() {



    const [attdata, setAtdata] = useState([]);
    const data = useLocation();
    const authtoken = data.state.authtoken;
    const uid=data.state.id;



    useEffect(() => {
        axios.get("/report/"+uid, {
            headers: {
                "AdminAuthToken": authtoken
            }
        })
            .then((res) => {
                setAtdata(res.data);
            })
            .catch(err => console.error(err));
    }, []);




    return (
        <div className='report'>

            <div className='date-div'>
                <label>From</label>
                <input type="datetime-local" />
                <label>To</label>
                <input type="datetime-local" />
            </div>
            <div className='row'>


                <div className="table-responsive col-6">

                    <table className="table table-success table-striped">

                        <thead>
                            <tr>
                                <th scope="col">No</th>
                                <th scope="col">Student id</th>
                                <th scope='col'>Student Name</th>
                                <th scope='col'> Student Email</th>
                                <th scope='col'> Total Presents</th>
                                <th scope='col'> Total Absents</th>
                                <th scope='col'> Total Leaves</th>

                            </tr>
                        </thead>
                        <tbody>

                            {
                                attdata.map((user, index) => {

                                    return <tr key={
                                        index
                                    }>
                                        <th scope="row">{index + 1}</th>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user._id}</td>

                                    </tr>
                                })
                            }


                        </tbody>
                    </table>
                </div>



            </div>

        </div >
    )
}
