import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, } from '@fortawesome/free-solid-svg-icons';
import { useLocation } from 'react-router-dom';
import { axios } from 'axios';





export default function Checkattendence() {
    const statedata = useLocation();
    const authtoken = statedata.state.authtoken;
    const studentid = statedata.state.studentid;

    useEffect(() => {

        axios
            .get("localhost:4000/fetchallattend",
                {
                    params: studentid
                }
                , {

                    headers: {
                        "AdminAuthToken": authtoken
                    }
                })
            .then((res) => {
                console.log(res.data);
            })
            .catch(err => console.error(err));
    }, [])

    return (
        <div className='viewatt-table-div'>

            <table class="table table-success table-striped ">

                <thead>
                    <tr>
                        <th scope="col">No</th>
                        <th scope='col'>date</th>
                        <th scope='col'>Status</th>
                        <th scope='col'>Action</th>

                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>12</td>
                        <td>present</td>

                        <td>
                            <FontAwesomeIcon icon={faEdit} />
                            <FontAwesomeIcon icon={faTrash} />
                        </td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>222</td>

                        <td>present</td>
                        <td>
                            <FontAwesomeIcon icon={faEdit} />
                            <FontAwesomeIcon icon={faTrash} />
                        </td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>222</td>

                        <td>present</td>
                        <td>
                            <FontAwesomeIcon icon={faEdit} />
                            <FontAwesomeIcon icon={faTrash} />
                        </td>
                    </tr>

                </tbody>
            </table>
            <div>
                <button className='btn btn-primary'>Mark attendence for this student</button>
            </div>

        </div >
    )
}
